// SPDX-License-Identifier: MIT
pragma solidity >=0.4.22 <0.9.0;


contract CourseMarketplace {

    enum State {
        Purchased,
        Activated,
        Deactivated
    }

    struct Course {
        uint id; //32 bytes
        uint price; //32 bytes
        bytes32 proof; //32 bytes
        address owner; //20 bytes
        State state; //1 byte
    }

    bool public isStopped = false;

    // mapping of courseHash to Course data
    mapping(bytes32 => Course) private ownedCourses;

    // mapping of courseId to courseHash
    mapping(uint => bytes32) private ownedCourseHash;

    // number of all courses + id of the course
    uint private totalOwnedCourses;

    address payable private owner;

    constructor() {
        setContractorOwner(msg.sender);
    }

    /// Course has invalid state!
    error InvalidState();

    /// Course is not created!
    error CourseIsNotCreated();

    /// Course has already a Owner!
    error CourseHasOwner();

		/// Sender is not Owner!
    error SenderIsNotCourseOwner();

    /// Only owner can set the owner
    error OnlyOwner();

    modifier onlyOwner {
        if (msg.sender != getContractOwner() ) {
            revert OnlyOwner();
        }
        _;
    }

	modifier onlyWhenNotStopped {
		require(!isStopped);
		_;
	}

	modifier onlyWhenStopped {
	require(isStopped);
	_;
	}

		receive() external payable {}

		function withdraw(uint amount)
			external
			onlyOwner
		 {
			 (bool success, ) = owner.call{value: amount}("");
			 require(success, "Withdraw failed");
		 }

		function emergencyWithdraw()
			external
			onlyWhenStopped
			onlyOwner
		{
			(bool success, ) = owner.call{value: address(this).balance}("");
			require(success, "Transfer failed");
		}

		function selfDestruct()
			external
			onlyWhenStopped
			onlyOwner
		{
			selfdestruct(owner);
		}

    function stopContract()
		  external
			onlyOwner
		{
				isStopped = true;
		}

		function resumeContract()
		  external
			onlyOwner
		{
				isStopped = false;
		}

    function purchaseCourse(
        bytes16 courseId,     // 0x00000000000000000000000000003130
        bytes32 proof         // 0x0000000000000000000000000000313000000000000000000000000000003130
    ) 
    	external 
			payable
			onlyWhenNotStopped
    {
        bytes32 courseHash = keccak256(abi.encodePacked(courseId, msg.sender));

        if(hasCourseOwnership(courseHash)) {
            revert CourseHasOwner();
        }

        uint id = totalOwnedCourses++;

        ownedCourseHash[id] = courseHash;
        ownedCourses[courseHash] = Course({
            id: id,
            price: msg.value,
            proof: proof,
            owner: msg.sender,
            state: State.Purchased
        });
    }

    function repurchaseCourse(bytes32 courseHash)
			external payable onlyWhenNotStopped {
					if(!isCourseCreated(courseHash)) {
							revert CourseIsNotCreated();
					}

					if(!hasCourseOwnership(courseHash)) {
							revert SenderIsNotCourseOwner();
					}

					Course storage course = ownedCourses[courseHash];

					if(course.state != State.Deactivated) {
							revert InvalidState();
					}

					course.state = State.Purchased;
					course.price = msg.value;
    }

    function activateCourse(
        bytes32 courseHash
    ) external onlyWhenNotStopped onlyOwner 
    {
      if (!isCourseCreated(courseHash)) {
        revert CourseIsNotCreated();
      }

      Course storage course = ownedCourses[courseHash];

      if(course.state != State.Purchased) {
          revert InvalidState();
      }

      course.state = State.Activated;
    }


    function deactivateCourse(bytes32 courseHash) 
    	external 
			onlyWhenNotStopped
			onlyOwner
    {
      if (!isCourseCreated(courseHash)) {
        revert CourseIsNotCreated();
      }

      Course storage course = ownedCourses[courseHash];

      if(course.state != State.Purchased) {
          revert InvalidState();
      }

      (bool success, ) = course.owner.call{ value: course.price }("");
      require(success, "Transfer failed!");

      course.state = State.Deactivated;
      course.price = 0;
    }

    function transferOwnership(address newOwner)
    external
    onlyOwner
    {
        setContractorOwner(newOwner);
    }

    function getCourseCount()
    external view returns (uint)
    {
        return totalOwnedCourses;
    }

    function getCourseHashAtIndex(uint index)
    external view returns (bytes32)
    {
        return ownedCourseHash[index];
    }

    function getCourseByHash(bytes32 courseHash)
    external view returns (Course memory)
    {
        return ownedCourses[courseHash];
    }

    function getContractOwner()
    public
    view
    returns (address)
    {
        return owner;
    }

    function setContractorOwner(address newOwner) private {
        owner = payable(newOwner);
    }

    function isCourseCreated(bytes32 courseHash) private view 
      returns (bool) {
      return ownedCourses[courseHash].owner != 0x0000000000000000000000000000000000000000;
    }

    function hasCourseOwnership(bytes32 courseHash)
    private view returns (bool)
    {
        return ownedCourses[courseHash].owner == msg.sender;
    }

}
